    import sys
    n, l = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))
    a.sort()

    max_d = 0.0

    # Check the first lantern's coverage from 0
    if a[0] > 0:
        max_d = max(max_d, a[0])

    # Check the gaps between consecutive lanterns
    for i in range(1, n):
        gap = a[i] - a[i-1]
        required_d = gap / 2.0
        if required_d > max_d:
            max_d = required_d

    # Check the last lantern's coverage to l
    if a[-1] < l:
        required_d = l - a[-1]
        if required_d > max_d:
            max_d = required_d

    print("{0:.10f}".format(max_d))

