import sys

def read_input():
    n, l = map(int, input().split())
    a = list(map(int, input().split()))
    return n, l, a

def solve(n, l, a):
    a.sort()

    max_distance_from_left = 0
    for i in range(n):
        if i == 0:
            max_distance_from_left = max(max_distance_from_left, a[i])
        else:
            max_distance_from_left = max(max_distance_from_left, (a[i] - a[i-1])/2)

    max_distance_from_right = 0
    for i in range(n-1, -1, -1):
        if i == n-1:
            max_distance_from_right = max(max_distance_from_right, l-a[i])
        else:
            max_distance_from_right = max(max_distance_from_right, (a[i+1] - a[i])/2)

    max_distance = max(max_distance_from_left, max_distance_from_right)
    if max_distance == 0:
        return 0

    return max_distance

def main():
    t = 1  # number of test cases
    for _ in range(t):
        n, l, a = read_input()
        result = solve(n, l, a)
        print(f"{result:.10f}")

if __name__ == "__main__":
    main()

