import sys
from math import ceil

def read_input():
    n, l = map(int, input().split())
    a = list(map(int, input().split()))
    return n, l, a

def find_min_radius(n, l, a):
    a.sort()
    max_dist = 0
    for i in range(n):
        if i == 0:
            max_dist = max(max_dist, a[i] + l - a[-1])
        else:
            max_dist = max(max_dist, a[i] - a[i-1], a[-1] - a[i-1])

    left = 0
    right = l
    while right - left > 1e-9:
        mid = (left + right) / 2
        covered = 0
        for i in range(n):
            if a[i] - mid >= 0:
                covered += ceil((a[i] + mid) - (a[0] - mid))
            if a[-i-1] + mid <= l:
                covered += ceil((a[-i-1] + mid) - (a[-1] + mid))
        if covered >= n:
            right = mid
        else:
            left = mid
    return right

