def solve(n, l, r):
    freq = {}
    for i in range(n):
        if l[i] != '?' and r[i] != '?':
            if l[i] not in freq or r[i] not in freq[l[i]]:
                freq[l[i]] = {l[i]: 1}
            elif freq[l[i]][l[i]] < 2:
                freq[l[i]][l[i]] += 1
        elif l[i] == '?' or r[i] == '?':
            if l[i] != '?' and r[i] not in freq:
                freq[r[i]] = {r[i]: 1}
            elif r[i] != '?' and l[i] not in freq:
                freq[l[i]] = {l[i]: 1}
            elif r[i] != '?' and l[i] in freq and r[i] not in freq[l[i]]:
                freq[l[i]][r[i]] = 1
            elif l[i] != '?' and r[i] in freq and l[i] not in freq[r[i]]:
                freq[r[i]][l[i]] = 1
    pairs = []
    for k in freq:
        if len(freq[k]) > 1:
            sorted_freq = sorted(freq[k].items(), key=lambda x: (-x[1], x[0]))
            for i in range(1, len(sorted_freq)):
                if sorted_freq[i][1] == 1:
                    pairs.append((sorted_freq[i-1][0], sorted_freq[i][0]))
                    sorted_freq[i-1] = (sorted_freq[i-1][0], sorted_freq[i-1][1] - 1)
                    sorted_freq[i] = (sorted_freq[i][0], sorted_freq[i][1] - 1)
    for k in freq:
        if len(freq[k]) == 1:
            pairs.append((k, list(freq[k].keys())[0]))
    return pairs

