def read_input():
    n = int(input())
    l = input()
    r = input()
    return n, l, r

def solve(n, l, r):
    # Initialize the pairs of boots with compatible colors
    pairs = [[] for _ in range(26)]

    # Count the number of each color
    count = [0] * 26
    for i in range(n):
        if l[i] != '?':
            count[ord(l[i]) - ord('a')] += 1
        if r[i] != '?':
            count[ord(r[i]) - ord('a')] += 1

    # Find the maximum number of pairs for each color
    for i in range(26):
        if l[i] == '?' or r[i] == '?':
            pairs[i].append((i, -1))
        elif l[i] == r[i]:
            pairs[i].append((i, i))
            count[i] -= 1

    # Try to find a pair for each left boot
    left_pairs = []
    for i in range(n):
        if l[i] == '?':
            for j in range(26):
                if r[i] == '?' or r[i] == chr(j + ord('a')):
                    left_pairs.append((i + 1, i + 1))
                    count[j] -= 1
                    break
        elif r[i] == '?':
            for j in range(26):
                if l[i] == chr(j + ord('a')):
                    left_pairs.append((i + 1, i + 1))
                    count[j] -= 1
                    break
        else:
            continue

    # Try to find a pair for each right boot
    for i in range(26):
        if count[i] > 0:
            while count[i] > 0 and len(pairs[i]) > 0:
                pair = pairs[i].pop()
                if pair[1] == -1:
                    pairs[i].append(pair)
                    break
                left_pairs.append((pair[1] + 1, pair[0] + 1))
                count[i] -= 1

    return left_pairs

def main():
    n, l, r = read_input()
    pairs = solve(n, l, r)
    print(len(pairs))
    for pair in pairs:
        print(*pair)

if __name__ == "__main__":
    main()

