import bisect

def main():
    n, x = map(int, sys.stdin.readline().split())
    vouchers = []
    for idx in range(n):
        l, r, c = map(int, sys.stdin.readline().split())
        vouchers.append((l, r, c, idx))

    from collections import defaultdict
    groups = defaultdict(list)
    for v in vouchers:
        d = v[1] - v[0] + 1
        groups[d].append(v)

    preprocessed_groups = {}
    for d in groups:
        sorted_end = sorted(groups[d], key=lambda x: x[1])
        ri_list = [v[1] for v in sorted_end]
        prefix_min = []
        current_min = float('inf')
        for v in sorted_end:
            current_min = min(current_min, v[2])
            prefix_min.append(current_min)

        sorted_start = sorted(groups[d], key=lambda x: x[0])
        li_list = [v[0] for v in sorted_start]
        suffix_min = []
        if len(sorted_start) > 0:
            suffix_min = [0] * len(sorted_start)
            suffix_min[-1] = sorted_start[-1][2]
            for i in range(len(sorted_start)-2, -1, -1):
                suffix_min[i] = min(sorted_start[i][2], suffix_min[i+1])
        preprocessed_groups[d] = {
            'sorted_end': (ri_list, prefix_min),
            'sorted_start': (li_list, suffix_min)
        }

    min_total = float('inf')
    for a in vouchers:
        a_l, a_r, a_cost, a_idx = a
        dA = a_r - a_l + 1
        req_d = x - dA
        if req_d < 0 or req_d == 0:
            continue
        if req_d not in preprocessed_groups:
            continue

        group_info = preprocessed_groups[req_d]
        sorted_end_ri, sorted_end_cm = group_info['sorted_end']
        sorted_start_li, sorted_start_sm = group_info['sorted_start']

        # Case 1: B ends before A starts (rj <= a_l)
        idx = bisect.bisect_right(sorted_end_ri, a_l) - 1
        if idx >= 0:
            min_cost_case1 = sorted_end_cm[idx]
            total = a_cost + min_cost_case1
            if total < min_total:
                min_total = total

        # Case 2: B starts after A ends (lj >= a_r)
        idx = bisect.bisect_left(sorted_start_li, a_r)
        if idx < len(sorted_start_li):
            min_cost_case2 = sorted_start_sm[idx]
            total = a_cost + min_cost_case2
            if total < min_total:
                min_total = total

    if min_total == float('inf'):
        print(-1)
    else:
        print(min_total)

