import math

def is_snowflake(n):
    if n < 1:
        return False
    if n == 1:
        return False
    # We need to find k>1 and m>=1 such that n = 1 + k*(1 + k + k^2 + ... +k^{m-1})
    # Let's express it as n = 1 + k * s, where s is a sum of k^i for i=0 to m-1
    # So s = (k^m - 1)/(k-1)
    # So n = 1 + k * (k^m - 1)/(k-1)
    # Rearranging: (n -1)(k-1) = k*(k^m - 1)
    # We can try different k values and see if m is an integer >=1
    max_k = int(math.isqrt(n)) + 1
    for k in range(2, max_k):
        remaining = n - 1
        s = remaining // k
        # Check if (remaining % k) is zero
        if remaining % k != 0:
            continue
        # Now s = (k^m -1)/(k-1)
        # So s must be >=1 because m>=1, so k^m >=k, so s >= (k -1)/(k-1) =1
        # So compute m
        numerator = s * (k - 1) + 1
        if numerator <= 0:
            continue
        if numerator % k != 0:
            continue
        m = 0
        while True:
            if numerator == k**m:
                m += 1
                break
            else:
                break
        if m >=1:
            return True
    return False

