from collections import deque

def read_input():
    """Read input from standard input."""
    n, m = map(int, input().split())
    graph = {}
    for i in range(m):
        u, v, c = map(int, input().split())
        if u not in graph:
            graph[u] = []
        if v not in graph:
            graph[v] = []
        graph[u].append((v, c))
        graph[v].append((u, c))
    return n, m, graph

def bfs(graph, s, t, parent):
    """Perform BFS to find augmenting paths."""
    visited = [False] * (len(graph) + 1)
    queue = deque()
    queue.append(s)
    visited[s] = True
    while queue:
        u = queue.popleft()
        for v, _ in graph[u]:
            if not visited[v]:
                queue.append(v)
                visited[v] = True
                parent[v] = u
                if v == t:
                    return True
    return False

def ford_fulkerson(graph, source, sink):
    """Implement Ford-Fulkerson algorithm to find maximum flow."""
    parent = [-1] * (len(graph) + 1)
    max_flow = 0
    while bfs(graph, source, sink, parent):
        path_flow = float('inf')
        s = sink
        while s != source:
            path_flow = min(path_flow, next((c for v, c in graph[parent[s]][::-1] if v == s), 0))
            s = parent[s]
        max_flow += path_flow
        v = sink
        while v != source:
            u = parent[v]
            for i, (neighbour, capacity) in enumerate(graph[u]):
                if neighbour == v:
                    graph[u][i] = (neighbour, capacity - path_flow)
                    break
            for i, (neighbour, capacity) in enumerate(graph[v]):
                if neighbour == u:
                    graph[v][i] = (neighbour, capacity + path_flow)
                    break
            v = parent[v]
    return max_flow

def solve(n, m, graph):
    """Solve the problem by finding the maximum flow and determining edge directions."""
    max_flow = ford_fulkerson(graph, 1, n)
    result = []
    for i in range(1, n + 1):
        if i not in graph:
            continue
        for j, (v, c) in enumerate(graph[i]):
            if v in graph[i][:j]:
                result.append('0' if c == 0 else '1')
            else:
                result.append('1' if c == 0 else '0')
    return '\n'.join(result)

