def bfs(graph, parent):
    """Perform BFS to find the maximum flow using the Ford-Fulkerson algorithm."""
    queue = deque([1])
    while queue:
        node = queue.popleft()
        for neighbor, _ in graph[node]:
            if neighbor != parent[node] and (neighbor not in parent or parent[neighbor] == -1):
                parent[neighbor] = node
                queue.append(neighbor)
    return parent

def max_flow(graph):
    """Calculate the maximum flow using the Ford-Fulkerson algorithm."""
    parent = {}
    max_flow = 0
    while bfs(graph, parent):
        path_flow = float('inf')
        s = len(graph)
        node = s
        for i in range(s - 1, 0, -1):
            if i not in parent:
                continue
            path_flow = min(path_flow, graph[parent[i]][0][1])
        max_flow += path_flow
        v = s
        for i in range(s - 1, 0, -1):
            if i not in parent:
                continue
            parent[i] = -1
            v = i
            while v != s:
                u = parent[v]
                graph[u][0][1] -= path_flow
                graph[v].remove((u, graph[u][0][1]))
                graph[u].append((v, graph[u][0][1]))
                v = u
    return max_flow

