import sys
from collections import deque

def parse_structure(s):
    nodes = {}
    children = {}
    idx = 0
    n = len(s)
    for i in range(n):
        c = s[i]
        if c == '?':
            var = f'v{i}'
            nodes[var] = {'type': 'var'}
            children[var] = []
            nodes[var]['children'] = []
            nodes[var]['parent'] = None
        elif c == '(':
            var = f'v{n}'
            nodes[var] = {'type': 'node'}
            children[var] = []
            nodes[var]['children'] = []
            parent = f'v{n-1}'
            nodes[parent]['children'].append(var)
            nodes[var]['parent'] = parent
        elif c == ')':
            pass
        elif c == '-':
            var = f'v{n}'
            nodes[var] = {'type': 'node'}
            children[var] = []
            parent = f'v{n-1}'
            nodes[parent]['children'].append(var)
            nodes[var]['parent'] = parent
        elif c == '+':
            var = f'v{n}'
            nodes[var] = {'type': 'node'}
            children[var] = []
            parent = f'v{n-1}'
            nodes[parent]['children'].append(var)
            nodes[var]['parent'] = parent
        elif c == '>':
            var = f'v{n}'
            nodes[var] = {'type': 'end'}
        else:
            var = f'v{n}'
            nodes[var] = {'type': 'node'}
            children[var] = []
            parent = f'v{n-1}'
            nodes[parent]['children'].append(var)
            nodes[var]['parent'] = parent
        idx += 1
    return nodes, children

def main():
    s = sys.stdin.read().strip()
    nodes, children = parse_structure(s)

    in_degree = {var: 0 for var in nodes}
    for child in children:
        in_degree[child] = 1

    queue = deque()
    for var in nodes:
        if in_degree[var] == 0:
            queue.append(var)

    topo_order = []
    while queue:
        u = queue.popleft()
        topo_order.append(u)
        for v in children[u]:
            in_degree[v] -= 1
            if in_degree[v] == 0:
                queue.append(v)

    if len(topo_order) != len(nodes):
        print("false")
        return

    var_values = {}
    for var in topo_order:
        var_values[var] = 0

    current_max = 10
    step = 0
    while True:
        used = set()
        for var in topo_order:
            if var not in used:
                used.add(var)
                current_max += 1
                if current_max > 10:
                    break
        if current_max > 10:
            break

        new_values = {}
        for var in topo_order:
            new_values[var] = str(var_values[var])

        valid = True
        for var in topo_order:
            for child in children.get(var, []):
                if child not in new_values or int(new_values[child]) < int(new_values[var]):
                    valid = False
                    break
            if not valid:
                break
        if valid:
            result = ''.join(new_values.values())
            print(result)
            return
        else:
            for var in topo_order:
                var_values[var] += 1
                if var_values[var] >= 10:
                    var_values[var] = 0
            if any(var_values[var] >= 10 for var in topo_order):
                print("false")
                return

if __name__ == "__main__":
    main()

