def main():
    s = sys.stdin.read().strip()
    stack = []
    for c in s:
        if c == '?':
            stack.append('var')
        elif c == '>':
            if not stack:
                print("false")
                return
            var = stack.pop()
            stack.append(f'(out {var})')
        elif c == '<':
            if not stack:
                print("false")
                return
            var = stack.pop()
            stack.append(f'(in {var})')
        elif c in '+-*/%&@#$':
            if len(stack) < 2:
                print("false")
                return
            a = stack.pop()
            b = stack.pop()
            op = c
            stack.append(f'(op {a} {b} {op})')
        else:
            if len(stack) >= 1:
                prev = stack[-1]
                stack[-1] = f"{prev}_{c}"
            else:
                stack.append(c)
    if len(stack) != 1:
        print("false")
        return

    var_map = {}
    for var in reversed(stack):
        if var.startswith('out'):
            var_name = var.split()[1]
            var_map[var_name] = (0, None)
        elif var.startswith('in'):
            var_name = var.split()[1]
            var_map[var_name] = (1, None)
        elif var.startswith('op'):
            parts = var.split()
            a = parts[1]
            b = parts[2]
            op = parts[3]
            if a not in var_map or b not in var_map:
                print("false")
                return
            a_info = var_map[a]
            b_info = var_map[b]
            if a_info[1] is not None:
                if a_info[1] != b:
                    print("false")
                    return
            if b_info[1] is not None:
                if b_info[1] != a:
                    print("false")
                    return
            var_map[op] = (min(a_info[0], b_info[0]), max(a_info[0], b_info[0]))

    var_order = []
    for var in sorted(var_map.keys()):
        var_order.append(var)

    var_values = {var: 0 for var in var_order}
    for var in reversed(var_order):
        info = var_map[var]
        a = info[0]
        b = info[1]
        if b is None:
            continue
        target = var
        val = (a + b) % MOD
        var_values[target] = val

    result = []
    for var in var_order:
        result.append(str(var_values[var]))
    print(''.join(result))

