import sys

def main():
    s = sys.stdin.read().strip()
    n = len(s)
    variables = []
    for i in range(n):
        c = s[i]
        if c.isdigit():
            variables.append((i, int(c)))

    # Create a list of variables with their initial values
    var_pos = {}
    for idx, pos in enumerate(variables):
        var_pos[pos] = (idx, variables[idx][1])

    # Now, simulate the Befunge program and track for each variable its last write operation
    # We'll represent each variable's last write as an operation: op = (command, read_vars)
    # where read_vars is a list of variable positions that were read during this write
    last_write = {}
    for i in range(n):
        c = s[i]
        if c not in '+-*/><':
            continue

        current_pos = (i, 0)
        while True:
            x = var_pos.get(current_pos, None)
            if x is None:
                break
            current_val = x[1]
            next_pos = (i+1, 0)
            next_x = var_pos.get(next_pos, None)
            if next_x is None:
                break
            next_var_idx = var_pos[next_pos][0]
            next_var_initial = next_x[1]

            if c == '+':
                op = ('+', [current_pos])
            elif c == '-':
                op = ('-', [current_pos])
            elif c == '*':
                op = ('*', [current_pos])
            elif c == '/':
                if current_val == 0:
                    # Division by zero, invalid
                    print("false")
                    return
                op = ('/', [current_pos])
            else:
                # Movement commands, no effect on variables
                pass

            # Update the next variable's last write
            key = next_pos
            if key not in last_write:
                last_write[key] = op
            else:
                last_write[key] = op  # overwrite with the last write

            # Move the pointer according to the command
            if c == '>':
                i += 1
            elif c == '<':
                i -= 1
            else:
                # '>', '<', etc. only move the pointer, no effect on variables
                pass
            # After moving, check if we should stop
            if i < 0 or i >= n:
                break

    # Now, for each variable, collect its last write operation
    # Each variable's value is determined by its last write operation
    # We need to model the dependencies and solve the system
    # Let's create a graph where each variable points to the variables it depends on
    # Also, keep track of each variable's equation: v = v0 + sum of terms

    # First, check for any division by zero cases
    for var_pos, info in last_write.items():
        op = info
        if op[0] == '/' and any(x in var_pos for x in op[1]):
            print("false")
            return

    # Now, build the system of equations
    # Each equation is var = var0 + c1*v1 + c2*v2 + ...
    # We can represent this as a dictionary where each variable maps to its equation
    equations = {}
    for var_pos, info in last_write.items():
        op = info
        var_idx, var_initial = var_pos
        cmd = op[0]
        read_vars = op[1]

        # Compute the coefficient for each read variable
        coeff = {}
        for rv in read_vars:
            rv_pos, rv_initial = rv
            coeff[rv_pos] = 0
        # Add the contribution from each read variable
        if cmd == '+':
            for rv in read_vars:
                coeff[rv] += 1
        elif cmd == '-':
            for rv in read_vars:
                coeff[rv] -= 1
        elif cmd == '*':
            for rv in read_vars:
                coeff[rv] *= 1
        elif cmd == '/':
            for rv in read_vars:
                coeff[rv] *= 1

        # The equation is var = var_initial + sum(coeff[var] * var_value)
        equations[var_pos] = {
            'initial': var_initial,
            'coefficients': coeff
        }

    # Now, we need to solve this system
    # First, check if there are any variables with no dependencies (free variables)
    free_vars = []
    for var_pos, eq in equations.items():
        if not eq['coefficients']:
            free_vars.append(var_pos)

    # Assign free variables to 0
    assigned = {}
    for v in free_vars:
        assigned[v] = 0

    # Now, process the dependent variables
    # We can represent each variable's value as a function of others
    # But since each variable is determined by the last write, which is a single equation
    # We can process them in an order where all dependencies are resolved

    # To do this, we can perform a topological sort based on dependencies
    # Build the dependency graph
    dependencies = {}
    for var_pos, eq in equations.items():
        deps = set()
        for rv in eq['coefficients']:
            if eq['coefficients'][rv] != 0:
                deps.add(rv)
        dependencies[var_pos] = deps

    # Check for cycles in dependencies
    visited = set()
    cycle_found = False
    for var_pos in equations:
        if var_pos in visited:
            continue
        stack = [var_pos]
        visited.add(stack[-1])
        while stack:
            current = stack.pop()
            if current in visited:
                continue
            if current in dependencies:
                if current in stack:
                    cycle_found = True
                    break
                for dep in dependencies[current]:
                    if dep not in visited:
                        stack.append(dep)
        if cycle_found:
            print("false")
            return

    # Now, perform a topological sort
    topo_order = []
    visited = set()
    stack = []
    for var_pos in equations:
        if var_pos not in visited:
            stack.append(var_pos)

    while stack:
        current = stack.pop()
        if current in visited:
            continue
        visited.add(current)
        topo_order.append(current)
        for dep in dependencies.get(current, []):
            if dep not in visited:
                stack.append(dep)

    if len(topo_order) != len(equations):
        print("false")
        return

    # Now, assign variables based on topo order
    for var_pos in topo_order:
        eq = equations[var_pos]
        initial = eq['initial']
        coeffs = eq['coefficients']
        sum_val = initial
        for rv in coeffs:
            if rv in assigned:
                sum_val += assigned[rv] * coeffs[rv]
            else:
                # This should have been handled by topo order
                pass
        assigned[var_pos] = sum_val

    # Now, check if all assigned values are between 0 and 9
    for v in assigned.values():
        if not (0 <= v <= 9):
            print("false")
            return

    # Now, collect the variables in the order of their positions
    result = []
    for var in variables:
        result.append(str(assigned[var]))

    print(''.join(result))

