            index += 1
            continue
        if c == '"':
            index += 1
            continue
        index += 1

    edges = {}
    for idx, var in nodes:
        edges[var] = []

    queue = deque()
    queue.append(([], 0))
    visited = set()
    result = None

    while queue:
        current_vars, path = queue.popleft()
        if len(current_vars) > len(edges) or any(var not in edges for var in current_vars):
            continue
        if all(var in current_vars for var in edges):
            if not result or path < result:
                result = path
            continue
        for var in edges:
