            possible_values = [str(i) for i in range(10)]
            for val in possible_values:
                new_vars = current_vars.copy()
                new_vars[var] = val
                new_path = path + [val]
                if tuple(new_vars) in visited:
                    continue
                visited.add(tuple(new_vars))
                queue.append((new_vars, new_path))
        if not queue:
            break

    if result:
        print(''.join(result))
    else:
        print("false")

