import sys

def read_input():
    return sys.stdin.readline().strip()

def parse_befunge_string(s):
    """
    Parse a Befunge string into a sequence of instructions.

    :param s: The Befunge string to parse.
    :return: A list of instructions.
    """
    instructions = []
    i = 0
    while i < len(s):
        if s[i] == '"':
            # Handle quoted strings
            j = s.find('"', i + 1)
            if j == -1:
                raise ValueError("Unbalanced quotes")
            instructions.append(s[i + 1:j])
            i = j + 1
        elif s[i] in '+-*/><,~|^':
            # Handle single-character instructions
            instructions.append(s[i])
            i += 1
        else:
            # Handle numeric literals
            j = i
            while j < len(s) and s[j].isdigit():
                j += 1
            instructions.append(int(s[i:j]))
            i = j
    return instructions

def find_min_sequence(instructions):
    """
    Find the lexicographically smallest sequence of variable values that can be fed to the golorp.

    :param instructions: The sequence of instructions for the golorp.
    :return: The lexicographically smallest sequence of variable values.
    """
    def is_valid(sequence):
        i = 0
        for instruction in instructions:
            if instruction == '>':
                i += 1
            elif instruction == '<':
                i -= 1
            elif instruction == '+':
                i += 1
                sequence[i % len(sequence)] = (sequence[i % len(sequence)] + 1) % 10
            elif instruction == '-':
                i += 1
                sequence[i % len(sequence)] = (sequence[i % len(sequence)] - 1) % 10
            elif instruction == ',':
                i += 1
                sequence[i % len(sequence)] = ord(input()[0]) - ord('0')
            elif instruction == '.':
                return ''.join(map(str, sequence))
        return False

    sequence = [0] * max(i for i in range(len(instructions)) if instructions[i] == '>' or instructions[i] == '<')
    for digit in '0123456789':
        sequence[0] = int(digit)
        if is_valid(sequence):
            return sequence
    return None

def solve():
    s = read_input()
    instructions = parse_befunge_string(s)
    sequence = find_min_sequence(instructions)
    if sequence is not None:
        print(''.join(map(str, sequence)))
    else:
        print('false')

