def parse_instruction(instruction):
    ops = {
        '+': lambda x, y: (x + y) % 10,
        '-': lambda x, y: (x - y) % 10,
        '*': lambda x, y: (x * y) % 10,
        '/': lambda x, y: (x // y) % 10,
