def find_ns(m):
    left, right = 0, 5 * m  # Initial guess for upper bound
    while count_trailing_zeros(right) < m:
        right *= 2
    # Binary search between left and right
    while left < right:
        mid = (left + right) // 2
        cz = count_trailing_zeros(mid)
        if cz < m:
