import sys
from collections import deque

def main():
    sys.setrecursionlimit(1 << 25)
    n = int(sys.stdin.readline())
    s = sys.stdin.readline().strip()

    total_teams = 1 << n
    for x in range(1, total_teams + 1):
        visited = [False] * (total_teams + 1)
        path = []
        current = x

        while True:
            if visited[current]:
                break
            visited[current] = True
            path.append((current, 0))
            pos = (current - 1) >> 1
            current = (pos >> 1) + 1

        opponent = {}
        level = 0
        for node in reversed(path):
            current, _ = node
            pos = (current - 1) >> 1
            next_node = (pos >> 1) + 1
            if next_node not in visited:
                opponent[current] = next_node
                level += 1
            else:
                break

        constraints = {}
        for i in range(n):
            if i in opponent:
                opp = opponent[i]
                c = s[i]
                if c == '0':
                    constraints[(i, opp)] = 'x < opp'
                else:
                    constraints[(i, opp)] = 'x > opp'

        in_degree = {}
        graph = {}
        cycle = False

        for (phase, opp) in constraints:
            cond = constraints[(phase, opp)]
            a, b = opp, phase
            if cond == 'x < opp':
                edge = (a, b)
                if edge not in graph:
                    graph[edge] = True
                    in_degree[b] = in_degree.get(b, 0) + 1
            elif cond == 'x > opp':
                edge = (b, a)
                if edge not in graph:
                    graph[edge] = True
                    in_degree[a] = in_degree.get(a, 0) + 1

        in_degree_x = 0
        in_degree_opp = 0
        for (phase, opp) in constraints:
            if phase == x:
                in_degree_x += 1
            else:
                in_degree_opp += 1

        queue = deque()
