def check(x, n, a):
    curr = x
    for i in range(n, 0, -1):
        if a[i] == 0:
            curr += 1 << (n - i)
        elif a[i] < (1 << (n - i)):
            return False
        else:
            curr -= 1 << (n - i)

    return curr <= (1 << n) - 1


