from collections import deque

def main():
    m, a, b = map(int, sys.stdin.readline().split())
    d = a
    if b != 0:
        d = gcd(a, b)
    M = a + b - d

    # Compute all reachable positions up to M
    reachable = set()
    reachable.add(0)
    q = deque([0])
    while q:
        y = q.popleft()
        next_y_a = y + a
        if next_y_a <= M:
            reachable.add(next_y_a)
            q.append(next_y_a)
        next_y_b = y - b
        if next_y_b >= 0:
            reachable.add(next_y_b)
            q.append(next_y_b)

    S = sorted(reachable)
    # Compute prefix sums
    prefix = [0] * (M + 1)
    for i in range(len(S)):
        x = S[i]
        if x > M:
            continue
        count = i + 1
        for j in range(x + 1, M + 1):
            if S[j] > x:
                break
            count += 1
        prefix[x] = count

    max_x = min(M, m)
    sum_part = 0
    for x in range(max_x + 1):
        if x <= M:
            sum_part += prefix[x]
        else:
            sum_part += (x + 1)

    if m >= M:
        additional = (m - M + 1) * (M + 1)
        sum_part += additional
    print(sum_part)

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

