from collections import defaultdict, deque

def main():
    sys.setrecursionlimit(1 << 25)
    n, k = map(int, sys.stdin.readline().split())
    edges = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        a, b = map(int, sys.stdin.readline().split())
        edges[a].append(b)
        edges[b].append(a)

    s = n - k
    nodes = list(range(1, n + 1))
    nodes.sort(reverse=True)
    selected = set(nodes[:s])

    visited = defaultdict(bool)
    q = deque()
    for node in nodes:
        if node in selected and not visited[node]:
            q.append(node)
            visited[node] = True
            while q:
                u = q.popleft()
                for v in edges[u]:
                    if v in selected and not visited[v]:
                        visited[v] = True
                        q.append(v)

    components = 0
    visited_sub = defaultdict(bool)
    for node in nodes:
        if node in selected and not visited_sub[node]:
            components += 1
            q = deque([node])
            visited_sub[node] = True
            while q:
                u = q.popleft()
                for v in edges[u]:
                    if v in selected and not visited_sub[v]:
                        visited_sub[v] = True
                        q.append(v)

    if components == 1:
        print(' '.join(map(str, [x for x in nodes[:s]])))
        return

    print(' '.join(map(str, [x for x in nodes if x not in selected])))
    return

