from collections import defaultdict

def dfs(node, parent, tree):
    for child in tree[node]:
        if child != parent:
            dfs(child, node, tree)

def find_removed_districts(n, k, edges):
    # Build the tree
    tree = defaultdict(list)
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    # Find the root of the tree
    root = 1
    while len(tree[root]) > 0:
        root += 1

    # Perform DFS from the root and mark the nodes in each subtree
    marked = [False] * (n + 1)
    dfs(root, -1, tree)

    # Sort the nodes based on their depth
    depths = []
    for i in range(1, n + 1):
        if not marked[i]:
            stack = [(i, 0)]
            while stack:
                node, depth = stack.pop()
                depths.append((node, depth))
                for child in tree[node]:
                    if not marked[child]:
                        stack.append((child, depth + 1))

    # Sort the depths in descending order
    depths.sort(key=lambda x: x[1], reverse=True)

    # Select the k nodes with the smallest depths
    removed = [x[0] for x in depths[:k]]

    return ' '.join(map(str, removed))

