        if child != parent:
            dfs(child, node, tree)

def find_max_power_set(tree):
    max_power = [0] * (n + 1)
    for i in range(1, n + 1):
        max_power[i] = max(max_power[i - 1], 2 ** i)
    return max_power

def find_min_cut(tree, k):
    max_power = find_max_power_set(tree)
    min_cut = float('inf')
    min_cut_node = None
    for i in range(1, n + 1):
        if k == 0:
            break
        power = max_power[i]
        if power > min_cut:
            continue
        visited = [False] * (n + 1)
        stack = [i]
        visited[i] = True
        while stack:
            node = stack.pop()
            for neighbor in tree[node]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    stack.append(neighbor)
        cut = sum(1 for v in visited if v)
        if cut < min_cut:
            min_cut = cut
            min_cut_node = i
        k -= 1
    return min_cut_node

