def main():
    import sys
    n, k, f = map(int, sys.stdin.readline().split())

    # Generate all possible binary strings up to length n
    max_len = n
    all_strings = []
    for l in range(1, max_len + 1):
        for s in ['0', '1']^l:
            all_strings.append(s)

    # Build the trie structure
    trie = {}
    for s in all_strings:
        node = trie
        for c in s:
            if c not in node:
                node[c] = {'children': {}, 'count': 0}
            node = node[c]
        node['is_leaf'] = True

    # Precompute the order of processing nodes
    order = []
    stack = [(trie, None)]
    while stack:
        current_node, parent = stack.pop()
        order.append(current_node)
        if parent is not None:
            parent['children'][current_node] = None
        else:
            pass
        for child in current_node.get('children', {}):
            stack.append((child, current_node))

    # Map each node to its index in the order list
    node_indices = {node: i for i, node in enumerate(order)}

    # Initialize DP table
    dp = [[0] * (n + 1) for _ in range(1 << len(order))]
    dp[0][0] = 1

    # Process each node in order
    for mask in range(1 << len(order)):
        for s in range(len(order)):
            if not (mask & (1 << s)):
                continue
            node = order[s]
            current_dp = dp[mask]
            if current_dp == 0:
                continue

            # Determine the maximum allowed count for this node's subtree
            max_allowed = k
            if node.get('is_leaf'):
                max_allowed = min(k, f - 0)  # itself can contribute to the sum
            else:
                max_allowed = min(k, f - sum(node.get('count') for child in node.get('children', {})))

            if max_allowed < 0:
                continue

            # The node's count can be from 0 to max_allowed
            for cnt in range(0, max_allowed + 1):
                new_mask = mask | (1 << s)
                if new_mask >= (1 << len(order)):
                    continue
                dp[new_mask][node.get('count', 0) + cnt] = (
                    dp[new_mask][node.get('count', 0) + cnt] + current_dp
                ) % MOD

    # Sum all states where the total count is exactly f
    result = 0
    for mask in range(1 << len(order)):
        if dp[mask][f] > 0:
            result = (result + dp[mask][f]) % MOD

    print(result)

