def main():
    import sys
    input = sys.stdin.read().split()
    n = int(input[0])
    k = int(input[1])
    f_target = int(input[2])

    len_map = {}
    total_len = 0
    for l in range(1, n+1):
        len_map[l] = (0, 0)
        if l == 1:
            len_map[l] = (1, 1)
        else:
            len_map[l] = (len_map[l-1][1] + 1, len_map[l-1][0] + 1)

    max_t = 0
    for l in len_map:
        if l > max_t:
            max_t = l
    power = [1] * (max_t + 1)
    for i in range(1, max_t + 1):
        power[i] = (power[i-1] * 2) % MOD

    m = sum(1 << l for l in range(1, n+1))
    if f_target < 0 or f_target > k * m:
        print(0)
        return

    dp = [0] * (m + 1)
    dp[0] = 1
    for t in range(m):
        l = len_map[t]
        cnt = (power[n - l] - 1) % MOD
        for s in range(m, -1, -1):
            if s >= f_target:
                continue
            if s + cnt <= k:
                dp[s + cnt] = (dp[s + cnt] + dp[s]) % MOD
            else:
                dp[s + cnt] = (dp[s + cnt] + dp[s]) % MOD

    for s in range(m, -1, -1):
        if s > f_target:
            continue
        if s == f_target:
            res = dp[s]
        else:
            res = 0
        for t in range(m, -1, -1):
            if t == s:
                continue
            l_t = len_map[t]
            p = power[n - l_t]
            subtract = (p - 1) % MOD
            new_f = s - subtract
            if new_f < 0:
                continue
            if new_f > f_target:
                continue
            res += dp[new_f]
            res %= MOD
    print(res % MOD)

