def main():
    import sys
    sys.setrecursionlimit(1 << 25)
    n, k, f = map(int, sys.stdin.readline().split())
    max_len = n
    pow2 = [1] * (max_len + 1)
    for i in range(1, max_len + 1):
        pow2[i] = (pow2[i-1] * 2) % MOD

    # Generate all strings of lengths 1 to n
    from itertools import product
    strings = []
    for m in range(1, n+1):
        for bits in product('01', repeat=m):
            s = ''.join(bits)
            strings.append((m, s))

    # Preprocess: for each string, compute its proper prefixes
    # Also, for each string, precompute its maximum a_t among proper prefixes
    # We'll process the strings in order of increasing length
    # So, group them by their lengths
    len_groups = [[] for _ in range(n+1)]
    for m, s in strings:
        len_groups[m].append(s)

    # Now, for each string, collect all its proper prefixes
    proper_prefixes = {}
    for m, s in strings:
        prefixes = []
        for t in len_groups[m-1]:
            if s.startswith(t):
                prefixes.append(t)
        proper_prefixes[s] = prefixes

    # Now, process the strings in order of increasing length
    dp = [{} for _ in range(n+1)]  # dp[m] will track the state for strings of length m
    dp[0] = {0: 1}  # base case: no strings, sum 0

    for m in range(1, n+1):
        current_strings = len_groups[m]
        for s in current_strings:
            max_a_t = 0
            for t in proper_prefixes[s]:
                if t in dp[m-1]:
                    if dp[m-1][t] > max_a_t:
                        max_a_t = dp[m-1][t]
            a_high = pow2[n - m]
            low = max_a_t
            high = min(k, a_high)
            if low > high:
                continue  # no valid a_s, skip
            dp[m][s] = {}
            for a in range(low, high + 1):
                new_sum = f - a
                if new_sum < 0:
                    continue
                if m == n:
                    if new_sum == 0:
                        dp[m][s][a] = 1
                else:
                    if dp[m-1]:
                        for prev_sum in dp[m-1].values():
                            if (new_sum - prev_sum) >= 0:
                                total = (prev_sum * dp[m-1][prev_sum]) % MOD
                                dp[m][s][new_sum] = (dp[m][s].get(new_sum, 0) + total) % MOD
                    else:
                        dp[m][s][new_sum] = 1

    total = 0
    for s in strings:
        m, s_str = s
        if m == n:
            if f in dp[m][s_str]:
                total = (total + dp[m][s_str][f]) % MOD
    print(total % MOD)

