def pow_mod(a, b):
    res = 1
    while b > 0:
        if b & 1:
            res = (res * a) % MOD
        a = (a * a) % MOD
        b >>= 1
    return res

def inv(x):
    return pow_mod(x, MOD - 2)

class Matrix:
    def __init__(self, n):
        self.n = n
        self.data = [[0] * n for _ in range(n)]

    def mul(self, other):
        res = Matrix(self.n)
        for i in range(self.n):
            for j in range(self.n):
                for k in range(self.n):
                    res.data[i][j] += self.data[i][k] * other.data[k][j]
                    res.data[i][j] %= MOD
        return res

    def __str__(self):
        return '\n'.join([' '.join(map(str, row)) for row in self.data])

