def find_successful_students():
    n, m = map(int, input().split())
    best_at = [0] * n

    for i in range(n):
        marks = list(map(int, input()))
        max_mark = max(marks)
        for j in range(m):
            if marks[j] == max_mark:
                best_at[i] |= 1 << j

    return sum(1 for student in best_at if student != 0)


