def calculate_prize(A, n):
    prize = [0] * (n + 1)
    for i in range(2 * n):
        if i % 2 == 0:
            prize[i // 2] += int(A[i])
        else:
            prize[i // 2] -= int(A[i])
    return prize

def find_max_prize(prize):
    max_prize = [0] * (n + 1)
    max_prize[0] = prize[0]
    for i in range(1, n + 1):
        for j in range(i):
            max_prize[i] = max(max_prize[i], max_prize[j] + prize[i - j])
    return max_prize

def reconstruct_sequence(n, max_prize, prize):
    sequence = [''] * n
    used = [False] * (n + 1)
    used[0] = True
    for i in range(n, 0, -1):
        for j in range(i - 1, -1, -1):
            if used[j]:
                if max_prize[i] == max_prize[j] + prize[i - j]:
                    sequence[i - 1] = 'M' if j % 2 == 0 else 'H'
                    used[j - 1] = True
                    break
    return ''.join(sequence)

