import sys

def read_input():
    n = int(input())
    A = input()
    return n, A

def calculate_prize(A, n):
    prize = [0] * (n + 1)
    for i in range(2 * n):
        if i % 2 == 0:
            prize[i // 2] += int(A[i])
        else:
            prize[i // 2] -= int(A[i])
    return prize

def solve(n, A):
    prize = calculate_prize(A, n)
    dp = [[-1] * (n + 1) for _ in range(n + 1)]
    dp[0][0] = 0
    for i in range(1, 2 * n + 1):
        for j in range(min(i // 2 + 1, n + 1)):
            if dp[i - 1][j] != -1:
                dp[i][j] = max(dp[i][j], dp[i - 1][j])
                if j < n and i % 2 == 0:
                    dp[i][j + 1] = max(dp[i][j + 1], dp[i - 1][j] + prize[i // 2])
    ans = []
    i, j = 2 * n, n
    while i > 0 and j > 0:
        if dp[i][j] != dp[i - 1][j]:
            if i % 2 == 0 and j < n:
                ans.append('H' if j % 2 == 0 else 'M')
                j += 1
            else:
                ans.append('M' if j % 2 == 0 else 'H')
            i -= 1
        else:
            ans.append('M' if j % 2 == 0 else 'H')
            i -= 1
