import sys

def solve(n, x, cards):
    # Create a dictionary to store the count of each number from -x to x
    count = {i: 0 for i in range(-x, x + 1)}

    # Count the occurrences of each number in the input list
    for card in cards:
        count[card] += 1

    # Calculate the net count of positive numbers
    pos_count = sum(1 for c in count.values() if c > 0)

    # If the net count of positive numbers is even, return 0
    if pos_count % 2 == 0:
        return 0

    # Otherwise, return the difference between the net count of positive numbers and the net count of negative numbers
    neg_count = sum(1 for c in count.values() if c < 0)
    return abs(pos_count - neg_count)


def main():
    n, x = map(int, input().split())
    cards = list(map(int, input().split()))
    print(solve(n, x, cards))


