    if n == 1:
        print(0)
        return

    # Sort the array
    a_sorted = sorted(a)

    # Precompute prefix and suffix min and max
    prefix_min = [a_sorted[0]] * n
    prefix_max = [a_sorted[0]] * n

    for i in range(1, n):
        prefix_min[i] = min(prefix_min[i-1], a_sorted[i])
        prefix_max[i] = max(prefix_max[i-1], a_sorted[i])

    suffix_min = [a_sorted[-1]] * n
    suffix_max = [a_sorted[-1]] * n

    for i in range(n-2, -1, -1):
        suffix_min[i] = min(suffix_min[i+1], a_sorted[i])
        suffix_max[i] = max(suffix_max[i+1], a_sorted[i])

    min_instability = float('inf')

    # Now, check for each element: what happens when it's removed
    for i in range(n):
        current = a_sorted[i]

        # If current is not min or max, then new min is prefix_min[i-1] and new max is suffix_max[i+1]
        if current != a_sorted[0] and current != a_sorted[-1]:
            new_min = prefix_min[i-1]
            new_max = suffix_max[i+1]
            instability = new_max - new_min
            if instability < min_instability:
                min_instability = instability
        else:
            # If current is min, find the next min (prefix_min[i-1] if i>0)
            if current == a_sorted[0]:
                if i == 0:
                    next_min = a_sorted[1]
                else:
                    next_min = prefix_min[i-1]

                # If current is max, find next max (suffix_max[i+1] if i <n-1)
                if current == a_sorted[-1]:
                    if i == n-1:
                        next_max = a_sorted[n-2]
                    else:
                        next_max = suffix_max[i+1]

                # Compute new instability based on whether it's min or max being removed
                if current == a_sorted[0]:
                    new_min = next_min
                    new_max = a_sorted[-1]
                elif current == a_sorted[-1]:
                    new_min = a_sorted[0]
                    new_max = next_max
                else:
                    continue  # This shouldn't happen since current is either min or max

                instability = new_max - new_min
                if instability < min_instability:
                    min_instability = instability
    print(min_instability)

