def array_stabilization(n, a):
    a.sort()
    min_max_diff = a[-1] - a[0]
    left = 0
    right = n - 1
    while left < right:
        diff = a[right] - a[left]
        if diff < min_max_diff:
            min_max_diff = diff
        if diff == 0:
            break
        if a[right] - a[left + 1] < a[right - 1] - a[left]:
            left += 1
        else:
            right -= 1
    return min_max_diff

