def replace_digits(a, s):
    n = len(a)
    m = len(s)
    digits = list(map(int, s))
    digits.sort(reverse=True)

    max_digit = 9
    for digit in digits:
        if digit > max_digit:
            max_digit = digit

    for i in range(n):
        if int(a[i]) < max_digit:
            a = a[:i] + str(max_digit) + a[i+1:]
            max_digit -= 1
            if max_digit == 0:
                break

    return a

