def main():
    import sys
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    m_bin = sys.stdin.readline().strip()

    # Precompute the binary digits of m as a list, MSB first
    m_bits = [int(c) for c in m_bin]
    n_bits = len(m_bits)

    # We'll process bits from left (MSB) to right (LSB), starting from 0
    # dp[i][tight]: maximum sum when considering the first i bits, tight indicates if it's equal to m's prefix
    # Initialize DP table
    dp = [[-1 for _ in range(2)] for __ in range(n_bits + 1)]
    dp[0][1] = 0  # start with no bits taken, and tight is True

    for i in range(n_bits):
        for tight in [0, 1]:
            if dp[i][tight] == -1:
                continue  # Skip invalid states
            # Current max sum at position i, with tight state
            current_sum = dp[i][tight]
            # Try to take the current bit
            if m_bits[i] == 1:
                new_tight = tight
                new_sum = current_sum + a[i]
                if i + 1 >= n_bits:
                    if new_tight:
                        if new_sum <= m:
                            if dp[i+1][new_tight] < new_sum:
                                dp[i+1][new_tight] = new_sum
                    else:
                        if dp[i+1][new_tight] < new_sum:
                            dp[i+1][new_tight] = new_sum
                else:
                    if new_tight:
                        # The next bits must be <= m's bits
                        if new_sum > m:
                            pass  # Not allowed, since x can't exceed m
                        else:
                            if dp[i+1][new_tight] < new_sum:
                                dp[i+1][new_tight] = new_sum
                    else:
                        # After this, any bits can be set to maximize sum
                        if dp[i+1][new_tight] < new_sum:
                            dp[i+1][new_tight] = new_sum
            # Try not to take the current bit
            new_sum_notake = current_sum
            if i + 1 >= n_bits:
                if dp[i+1][tight] < new_sum_notake:
                    dp[i+1][tight] = new_sum_notake
            else:
                if tight:
                    # Next bits must be <= m's bits
                    if new_sum_notake > m:
                        pass
                    else:
                        if dp[i+1][tight] < new_sum_notake:
                            dp[i+1][tight] = new_sum_notake
                else:
                    # Can take anything
                    if dp[i+1][tight] < new_sum_notake:
                        dp[i+1][tight] = new_sum_notake
    # The answer is the maximum of dp[n_bits][0] and dp[n_bits][1]
    print(max(dp[n_bits][0], dp[n_bits][1]))

