def main():
    import sys
    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    idx += 1
    a = list(map(int, input[idx:idx+n]))
    idx +=n
    s = input[idx]
    # Compute m: s is the binary string, s[0] is MSB, s[-1] is LSB
    m = 0
    for i in range(n):
        m += int(s[i]) * (1 << (n-1 -i))

    INF = -float('inf')
    dp = [[INF] * 2 for _ in range(n+1)]
    dp[0][1] = 0  # initial state: pos=0, tight=True, sum=0

    for pos in range(n):
        for tight in [0, 1]:
            if dp[pos][tight] == INF:
                continue
            max_bit = (m >> pos) & 1 if tight else 1
            for bit_choice in [0, 1]:
                if bit_choice > max_bit:
                    continue
                new_tight = 0
                if tight:
                    if bit_choice == ((m >> pos) & 1):
                        new_tight = 1
                new_sum = dp[pos][tight] + a[bit_choice]
                if new_sum > dp[pos+1][new_tight]:
                    dp[pos+1][new_tight] = new_sum

    result = max(dp[n][0], dp[n][1])
    print(result)

