def calculate_max_value(n, a, m_str):
    max_val = 0
    m = 0
    for i in range(n):
        if m_str[i] == '1':
            max_val += a[n - i - 1]
            m += 2 ** (n - i - 1)
    total_sum = sum(a)
    max_val = min(max_val, total_sum)

    for i in range(n):
        if m_str[i] == '0':
            temp_m = m - 2 ** (n - i - 1)
            if temp_m >= 0:
                max_val = max(max_val, a[n - i - 1] + calculate_max_value(n, a, m_str[:i] + '1' + m_str[i+1:]))
    return max_val

