else:
    # Precompute factorial and inverse factorial up to n
    max_n = n
    fact = [1] * (max_n + 1)
    for i in range(1, max_n + 1):
        fact[i] = fact[i-1] * i % MOD

    inv_fact = [1] * (max_n + 1)
    inv_fact[max_n] = pow(fact[max_n], MOD-2, MOD)
    for i in range(max_n -1, -1, -1):
        inv_fact[i] = inv_fact[i+1] * (i+1) % MOD

    def comb(a, b):
        if a < b or b < 0:
            return 0
        return fact[a] * inv_fact[b] % MOD * inv_fact[a - b] % MOD

    dp = [0] * (n + 1)
    dp[0] = 1
    for i in range(1, n + 1):
        # For each possible starting position j of a tower
        for j in range(max(1, i - 200000), min(i, n - 200000) + 1):
            if j >= 1 and j <= n:
                p = min(j, n - j + 1)
                if i - p + 1 >= 1:
                    dp[i] = (dp[i] + dp[i - p]) % MOD
        # Additionally, check if placing a single tower covering exactly i
        if i >= 1 and i <= n:
            p = 1
            if i - p + 1 >= 1 and i + p -1 <= n:
                dp[i] = (dp[i] + dp[i - 1]) % MOD
        # Also, consider placing a tower at i with p=1, which covers only i
        if i >= 1:
            dp[i] += dp[i-1]
            dp[i] %= MOD

    total = pow(2, n, MOD)
    ans = dp[n] * pow(total, MOD-2, MOD) % MOD
    print(ans)

