import sys
from itertools import permutations

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    if n == 1:
        print(a[0])
        return

    # Function to check if a permutation b is valid
    def is_valid(b):
        # Precompute all subset sums for a and b
        a_subset = set()
        b_subset = set()

        # Iterate over all non-empty, non-full subsets
        for mask in range(1, (1 << n) - 1):
            sum_a = 0
            sum_b = 0
            for i in range(n):
                if mask & (1 << i):
                    sum_a += a[i]
                    sum_b += b[i]
            if sum_a == sum_b:
                return False
        return True

    # Generate all possible permutations and check
    for p in permutations(a):
        if is_valid(p):
            print(' '.join(map(str, p)))
            return

    # If no permutation found
    print(-1)

