def solve(n, a):
    # Sort the array in descending order
    a.sort(reverse=True)

    # Initialize the result array
    b = [0] * n

    # Try to place the largest numbers first
    for i in range(1, 2**n):
        temp = []
        for j in range(n):
            if (i & (1 << j)) > 0:
                temp.append(a[j])
        if len(temp) % 2 == 0:
            continue

        # Place the largest number at the position that will make the sum different
        b[temp.index(max(temp))] = a[0]

        # Place the rest of the numbers
        for j in range(n):
            if (i & (1 << j)) > 0:
                b[j] = temp.pop(0)

    # If we still haven't placed all numbers, it's impossible to construct the array
    if any(x == 0 for x in b):
        return -1

    return ' '.join(map(str, b))

