def compute_min_nodes(p):
    if p == 0:
        return (0, 0)
    # Find the largest k such that C(k, 2) <= p
    k = 1
    while True:
        c = k * (k - 1) // 2
        if c > p:
            break
        k += 1
    # Now we try to subtract the largest possible C(k,2) from p
    max_k = k
    total_nodes = 0
    remaining_p = p
    while remaining_p > 0:
        c = max_k * (max_k - 1) // 2
        if c > remaining_p:
            max_k -= 1
            c = max_k * (max_k - 1) // 2
        count = min(remaining_p // c, max_k)
        if count == 0:
            break
        total_nodes += count * max_k
        remaining_p -= count * c
        max_k = 0  # reset to find next possible k
    # After that, check if remaining_p is zero
    if remaining_p != 0:
        return (-1, -1)
    else:
        return (total_nodes, 0)

