import math

def minimal_n(p):
    if p == 0:
        return 0
    n = 0
    remaining_p = p
    while remaining_p > 0:
        max_k = 0
        for k in range(1, int(math.isqrt(remaining_p)) + 1):
            current = k * (k - 1) // 2
            if current <= remaining_p:
                max_k = k
            else:
                break
        if max_k == 0:
            break
        remaining_p -= max_k * (max_k - 1) // 2
        n += max_k
    return n

def max_unidirectional(n, p):
    return (n * n - n - 2 * p) // 2

