    def get_rook_next(r, c, visited):
        min_val = float('inf')
        min_pos = None
        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        for dr, dc in directions:
            nr, nc = r + dr, c + dc
            if 0 <= nr < N and 0 <= nc < N:
                if not visited[nr][nc]:
                    val = grid[nr][nc]
                    if val < min_val:
                        min_val = val
                        min_pos = (nr, nc)
        return min_pos

    # Function to get next move for queen
    def get_queen_next(r, c, visited):
        min_val = float('inf')
        min_pos = None
        for i in range(N):
