def is_valid(board, n, i, j, x, y):
    return 0 <= i < n and 0 <= j < n and board[i][j] == x

def find_min_unvisited(board, n, x, y):
    min_val = float('inf')
    min_pos = None
    for i in range(n):
        for j in range(n):
            if not board[i][j]:
                if board[x][y] < min_val and is_valid(board, n, i, j, x, y):
                    min_val = board[x][y]
                    min_pos = (i, j)
    return min_pos

def rook_move(board, n, x, y):
    min_pos = find_min_unvisited(board, n, x, y)
    if min_pos:
        board[min_pos[0]][min_pos[1]] = board[x][y]
        board[x][y] = 0
        return (min_pos[0], min_pos[1])
    return (x, y)

def queen_move(board, n, x, y):
