import sys

def print_board(n):
    if n == 1:
        print(1)
        return

    board = [[0 for _ in range(n)] for _ in range(n)]

    # Fill the first row
    for i in range(1, n * n + 1):
        if i % n == 0:
            board[0][n - 1] = i
            n -= 1
        else:
            board[0][i % n - 1] = i

    # Fill the rest of the board
    for i in range(1, n):
        for j in range(n):
            board[i][j] = (i - 1) * n + j + 1

    # Swap the last two elements of the first row
    board[0][-1], board[0][-2] = board[0][-2], board[0][-1]

    # Print the board
    for row in board:
        print(*row)

