    # Precompute max_diff[i][j] = max of a[i..j]
    max_diff = [[0] * n for _ in range(n)]
    for i in range(n-1, -1, -1):
        max_diff[i][i] = a[i]
        if i < n-1:
            max_diff[i][i+1] = max(max_diff[i+1][i+1], a[i])
            for j in range(i+2, n):
                max_diff[i][j] = max(max_diff[i][j-1], max_diff[i+1][j-1], a[i])

    # Initialize DP table
    dp = [[0] * (k+1) for _ in range(n+1)]

    # Base case: j=1, all in one day
    for i in range(1, n+1):
        dp[i][1] = max_diff[0][i-1]

    # Fill DP table
    for j in range(2, k+1):
        for i in range(j, n+1):
            max_val = 0
            for l in range(j-1, i):
                current_max = max_diff[l][i-1]
                if dp[l][j-1] + current_max > max_val:
                    max_val = dp[l][j-1] + current_max
            dp[i][j] = max_val

    # Now reconstruct the segments
    segments = []
    for j in range(k, 0, -1):
        last = n
        for i in range(j-1, n):
            if dp[i+1][j-1] == dp[last][j]:
                segments.append(last - i)
                last = i
        segments.append(last)

    # Convert to 0-based indices
    segments = [s for s in segments]
    print(dp[n][k])
    print(' '.join(map(str, segments)))

