def max_games(n):
    fib = [1, 1]
    while True:
        next_val = fib[-1] + fib[-2]
        if next_val > n:
            break
        fib.append(next_val)
    # Now find the largest k such that fib[k] <= n
    k = 0
    for i in range(len(fib)):
        if fib[i] <= n:
            k = i
        else:
            break
    return k

