import sys
from math import gcd
from collections import defaultdict

def is_square_free(n):
    if n == 0:
        return False
    for i in range(2, int(n**0.5)+1):
        if n % i * i == 0:
            return False
    return True

def main():
    input = sys.stdin.read().split()
    ptr = 0
    n = int(input[ptr])
    ptr += 1
    m = int(input[ptr])
    ptr += 1

    edges = []
    for _ in range(m):
        u = int(input[ptr])-1  # 0-based
        ptr +=1
        v = int(input[ptr])-1
        ptr +=1
        g = int(input[ptr])
        ptr +=1
        l = int(input[ptr])
        ptr +=1
        edges.append((u, v, g, l))

    # Check initial conditions
    possible = True
    k_list = []
    for u, v, g, l in edges:
        if l % g != 0:
            possible = False
        k = l // g
        if not is_square_free(k):
            possible = False
        k_list.append(k)

    if not possible:
        print("NO")
        return

    # Prepare for Union-Find
    parent = list(range(n))
    rank = [1]*n
    product = [1]*n  # product from root to node

    def find(u):
        while parent[u] != u:
            product[u] = product[parent[u]] * product[u]
            parent[u] = parent[parent[u]]
            u = parent[u]
        return u

    def union(u, v, k):
        root_u = find(u)
        root_v = find(v)

        if root_u == root_v:
            a_u = product[u] // product[root_u]
            a_v = product[v] // product[root_v]
            if a_u * a_v != k:
                return False
            if gcd(a_u, a_v) != 1:
                return False
            return True
        else:
            a_u = product[u] // product[root_u]
            a_v = product[v] // product[root_v]
            if a_u == 0 or a_v == 0:
                return False
            if k % (a_u * a_v) != 0:
                return False
            a_new = k // (a_u * a_v)
            if gcd(a_u, a_new) != 1 and gcd(a_v, a_new) != 1:
                return False
            if gcd(a_u, a_v) != 1:
                return False
            # Merge smaller set into larger set
            if rank[root_u] < rank[root_v]:
                parent[root_u] = root_v
                product[root_v] *= a_u * a_v * a_new
                rank[root_v] += rank[root_u]
            else:
                parent[root_v] = root_u
                product[root_u] *= a_u * a_v * a_new
                if rank[root_u] == rank[root_v]:
                    rank[root_u] += 1
            return True

    for idx in range(m):
        u, v, g, l = edges[idx]
        k = k_list[idx]
        if not union(u, v, k):
            possible = False
            break

    if not possible:
        print("NO")
        return

    # Now assign a_i values
    a = [1]*n
    visited = [False]*n
    for i in range(n):
        if visited[i]:
            continue
        if find(i) == i:
            stack = [(i, 1)]
            visited[i] = True
            while stack:
                node, val = stack.pop()
                a[node] = val
                for neighbor, k in edges:
                    if neighbor != node:
                        if not visited[neighbor]:
                            stack.append( (neighbor, k // val) )

    # Verify all a_i's
    for u, v, g, l in edges:
        k = l // g
        a_u = a[u]
        a_v = a[v]
        if a_u * a_v != k:
            possible = False
            break
        if gcd(a_u, a_v) != 1:
            possible = False
            break

    if possible:
        print("YES")
        print(' '.join(map(str, a)))
    else:
        print("NO")

