    for i in range(1, m + 1):
        u, v, gcd, lcm = map(int, lines[i].split())
        roads.append((u, v, gcd, lcm))
    return n, m, roads

def solve(n, m, roads):
    """Solves the problem using graph theory and number theory."""
    graph = defaultdict(list)
    degrees = [0] * (n + 1)

    for u, v, _, _ in roads:
        graph[u].append(v)
        graph[v].append(u)
        degrees[u] += 1
        degrees[v] += 1

    if any(deg == 1 for deg in degrees):
        raise ValueError("Graph has a cycle")

    parent = list(range(1, n + 1))

    def find(x):
        """Finds the representative of the set containing x."""
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        """Merges the sets containing x and y."""
        px = find(x)
        py = find(y)
        if px != py:
            parent[px] = py

    for u, v, gcd, lcm in roads:
        x = find(u)
        y = find(v)
        if x == y:
            continue
        union(u, v)
        if gcd != 1:
            raise ValueError("GCD is not 1")
        if lcm % gcd != 0:
            raise ValueError("LCM is not a multiple of GCD")

    representatives = {}
    for i in range(1, n + 1):
        representatives[find(i)] = i

    mushrooms = {}
    for u, v, _, lcm in roads:
        x = representatives[find(u)]
        y = representatives[find(v)]
        if x not in mushrooms:
            mushrooms[x] = lcm // gcd(lcm, v)
        elif y not in mushrooms:
            mushrooms[y] = lcm // gcd(lcm, v)
        else:
            raise ValueError("Duplicate edges")

    result = [mushrooms[i] for i in range(1, n + 1)]
    return result

