def min_ternary_string(s):
    # Convert the string into a list for easier manipulation
    s_list = list(s)

    # First pass: move all '0's as far left as possible
    i = 0
    while i < len(s_list) - 1:
        if s_list[i] == '1' and s_list[i + 1] == '0':
            s_list[i], s_list[i + 1] = s_list[i + 1], s_list[i]
        else:
            i += 1

    # Second pass: move all '2's as far left as possible
    j = len(s_list) - 1
    while j > 0:
        if s_list[j] == '1' and s_list[j - 1] == '2':
            s_list[j], s_list[j - 1] = s_list[j - 1], s_list[j]
        else:
            j -= 1

    return ''.join(s_list)

